package qlsv_swing.qlsv.services;import qlsv_swing.qlsv.dao.UserDao;import qlsv_swing.qlsv.entity.User;import qlsv_swing.qlsv.utils.FileUtils;import java.util.ArrayList;import java.util.List;public class UserService {    private UserDao userDao = new UserDao();    public boolean checkUserName(User user) {        if (user.getUserName().equals("") || user.getPassword().equals("")) {            return false;        }        if (userDao.checkUserNam(user.getUserName()) != null) {            return false;        }        return true;    }    public void add(User user) {        userDao.create(user);    }}